events { }
http {
    server {
        root /chartrepo;
        listen 443 ssl;
        server_name charts.example.com;
        ssl_certificate /certs/server.crt; ![1](Images/1.png)
        ssl_certificate_key /certs/server.key; ![2](Images/2.png)
        ssl_client_certificate /certs/client-ca.pem; ![3](Images/3.png)
        ssl_verify_client on;
        proxy_set_header SSL_CLIENT_CERT $ssl_client_cert;
    }
}
