CREATE PROCEDURE us_population()
BEGIN
  DECLARE done BOOLEAN DEFAULT FALSE; ![1](Images/1.png)
  DECLARE state_pop, total_pop BIGINT DEFAULT 0;
  DECLARE cur CURSOR FOR SELECT pop FROM states; ![2](Images/2.png)
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE; ![3](Images/3.png)

  OPEN cur;
  fetch_loop: LOOP
    FETCH cur INTO state_pop; ![4](Images/4.png)
    IF done THEN ![5](Images/5.png)
      LEAVE fetch_loop;
    END IF;
    SET total_pop = total_pop + state_pop; ![6](Images/6.png)
  END LOOP;
  CLOSE cur;
  SELECT total_pop AS 'Total U.S. Population'; ![7](Images/7.png)
END;
```

